mapper.select:
SELECT board_no, board_title, board_content, users_nickname, board_date, board_views, board_show, board_comments_count, board_category_no FROM ( SELECT board_no, board_title, board_content, users_nickname, board_date, board_views, board_show, board_comments_count, board_category_no, row_number() over(order by board_no desc) as rNum FROM board, users WHERE board.users_no = users.users_no and board_show = 0 and board_category_no = #{board_category_no}
and ${searchType} like '%' || #{keyword} || '%'
and (board_title like '%' || #{keyword} || '%' or board_content like '%' || #{keyword} || '%')
) b WHERE rNum between #{start} and #{end} ORDER BY board_no desc

mapper.update:
update board set board_title=#{board_title},
board_category_no = #{board_category_no},
board_content=#{board_content},
board_fname=#{board_fname},
board_fsize=#{board_fsize} 
where board_no = #{board_no}

mapper.select:
select count(*) FROM board, users WHERE board.users_no = users.users_no and board_show = 0 and board_category_no = #{board_category_no}
and ${searchType} like '%' || #{keyword} || '%'
and (board_title like '%' || #{keyword} || '%' or board_content like '%' || #{keyword} || '%')
